mapper.update:
update review
set content = #{content}
where rnum = #{rnum}

mapper.select:
	  select rnum, content, regdate, id, contentsno
	  from review
	  where contentsno = #{contentsno}
    	order by rnum DESC
 		limit #{sno}, #{eno}

select count(*) 
from review
where contentsno = #{contentsno}
